<?php		function enviar($asunto,$remitente,$body,$correo_desde,$correo_para,$nombre_para,$adjunto,$archivo_desde){				require_once 'mandrill-api-php/src/Mandrill.php';		if($adjunto!='sinadjunto'){			$sender=array(				array(					'type' => 'application/pdf',					'name' => 'Informacion.pdf',					'content' => base64_encode(file_get_contents($adjunto))				)			);		}else{			$sender='';		}				try {			$mandrill = new Mandrill(MANDRILL_API_KEY);			$message = array(				'html' => $body,				'subject' => $asunto,				'from_email' => $correo_desde,				'from_name' => $remitente,				'to' => array(					array(						'email' => $correo_para,						'name' => htmlentities($nombre_para)					)				),				'important' => false,				'track_opens' => true,				'track_clicks' => true,				'auto_text' => true,				'auto_html' => true,				'preserve_recipients' => true,				'merge' => true,				'merge_vars' => array(					array(						'rcpt' => $correo_para,					)				),'attachments' => $sender			);			$async = false;			$ip_pool = 'Main Pool';			$result = $mandrill->messages->send($message, $async, $ip_pool);		} catch(Mandrill_Error $e) {					}		}?>